@implementation ESGridViewController


-(void)viewDidLoad {
    [super viewDidLoad];
    
    ESGridView *_gridView = [[ESGridView alloc] initWithFrame:CGRectMake(10, 50, 200, 200)];
//    [_gridView setCurrentGridSize:CGSizeMake(20, 20)];
//    [_gridView setDefaultGridSize:CGSizeMake(20, 20)];
    [_gridView setMinEdgeInset:UIEdgeInsetsMake(5, 5, 5, 5)];
    [_gridView setMargin:5];
    [_gridView setMinimumGridZoomScale:0.5];
    [_gridView setMaximumGridZoomScale:1.0];
    [_gridView setAllowGridScaling:YES];

    /* Types
     * ESGridViewLayoutVertical = 0,
     * ESGridViewLayoutHorizontal,
     * ESGridViewLayoutHorizontalPagedLTR,   // LTR: left to right
     * ESGridViewLayoutHorizontalPagedTTB,    // TTB: top to bottom
     * ESGridViewLayoutVerticalPagedLTR,
     * ESGridViewLayoutVerticalPagedTTB
     */
    [_gridView setLayoutStrategyType:ESGridViewLayoutVertical];
    [_gridView setDelegate:self];
    [_gridView setDataSource:self];

    [_container addSubview:_gridView];
    
}

#pragma mark -
#pragma mark - GridView Delegate

#pragma mark - 
#pragma mark - GridView DataSource
-(NSInteger)numberOfTotalGrid:(ESGridView *)gridView {
    return [ImageList count];
}

-(ESGridViewCell *)gridView:(ESGridView *)gridView cellForIndex:(NSInteger)index {
    ESGridViewCell *cell = [gridView dequeueReusableCellWithIdentifier:@"CELL"];
    
    if (cell == nil) {
        cell = [[ESGridViewCell alloc] initWithSize:CGSizeMake(50, 50)];
        [cell setReuseIdentifier:@"CELL"];
        ESImageView *imageView = [[ESImageView alloc] initWithFrame:cell.bounds];
        [imageView setTag:10];
        [imageView setImageUrl:[ImageList objectAtIndex:index]];
        [cell addSubview:imageView];
    }
    
    ESImageView *imageView = (ESImageView*)[cell viewWithTag:10];
    [imageView setImageUrl:[ImageList objectAtIndex:index]];
    
    [cell setBackgroundColor:[UIColor grayColor]];
    return cell;
}

@end
